        window.removeEventListener('scroll', handleScroll);
      };
    });
    return(
        <>

        <Navbarr />
        <div id="page">
            <div id="landing">
                <div id="image" >
                    <Face_image  />
                </div>
                <div id="text" >
                    <Welcome_text  />
                </div>
            </div>



            <div id="cv">

                <div id="ausbildung">
                    <Ausbildung text="Ausbildung" />
                </div>

                <div  id="schule">
                    <Schule text="Schule" />
                </div>

                <div id="ueber_mich">
                    <Ueber_mich text="Ãœber mich" />
                </div>
                
                <div id="interessen">
                    <Interessen text="Meine Interessen" />
                </div>
            </div>
            
            <div id="skills">
                <Skills />
            </div>
            
            <Footer />
        </div>
        </>
    )
}

export default Home
